// API Connected (Live Data)
import ToDo from './components/todo/todo-connected.js';

function reducer(state, action){
  switch (action.type){
    case 'enter':
      return ([...state, state.words = action.payload.toUpperCase()])
    case 'reset':
      return (state = '')
  }
}

let initialState = ''
let state = {}
state.words = 'default words in state'

